package com.mg.compose.service.dto;import com.mg.common.pojo.CommonItem;import com.mg.common.pojo.QueryItem;import com.mg.compose.db.pojo.RmtUserInfo;import com.mg.compose.service.convert.util.QueryItemCommonUtil;import com.mg.compose.service.convert.util.RmtUserInfoCommonUtil;import org.springframework.stereotype.Service;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import java.util.List;import com.mg.compose.service.feign.UserFeign;@Service@Slf4jpublic class UserDTO {		@Autowired	QueryItemCommonUtil queryItemCommonUtil;		@Autowired	RmtUserInfoCommonUtil commonUtil;		@Autowired	UserFeign feign;		public RmtUserInfo get(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return commonUtil.toPojo(feign.get(item));	}		public RmtUserInfo edit(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return commonUtil.toPojo(feign.edit(item));	}		public List<RmtUserInfo> list(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return commonUtil.toPojoList(feign.list(item));	}		public RmtUserInfo delete(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return commonUtil.toPojo(feign.delete(item));	}		public RmtUserInfo login(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		return commonUtil.toPojo(feign.getbyname(item));	}}