package com.mg.compose.service.feign;import com.mg.common.pojo.CommonItem;import com.mg.compose.constant.feign.UserFeignConstant;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.RequestMapping;@FeignClient(name = UserFeignConstant.FEIGN_SERVER_NAME,fallback = UserFeignFallBack.class)public interface UserFeign {		@RequestMapping(UserFeignConstant. FEIGN_GET)	CommonItem get(CommonItem item);		@RequestMapping(UserFeignConstant. FEIGN_EDIT)	CommonItem edit(CommonItem item);		@RequestMapping(UserFeignConstant. FEIGN_LIST)	CommonItem list(CommonItem item);		@RequestMapping(UserFeignConstant. FEIGN_DELETE)	CommonItem delete(CommonItem item);		@RequestMapping(UserFeignConstant. FEIGN_GETBYNAME)	CommonItem getbyname(CommonItem item);}