package com.mg.api.service.dto;import com.mg.website.common.pojo.ApiResultItem;import com.mg.website.common.pojo.CommonItem;import com.shineon.api.common.base.convert.CommonItem;import com.shineon.api.common.base.db.ApiResultItem;import com.mg.api.db.pojo.QueryItem;import com.mg.api.service.convert.util.QueryItemCommonUtil;import com.mg.api.service.convert.util.RmtUserInfoCommonUtil;import com.mg.api.service.feign.UserFeign;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Service@Slf4jpublic class UserDTO {		@Autowired    QueryItemCommonUtil queryItemCommonUtil;		@Autowired    UserFeign feign;		@Autowired    RmtUserInfoCommonUtil commonUtil;		public ApiResultItem get(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		CommonItem result =  feign.get(item);		return new ApiResultItem(result ,commonUtil.toPojo(result));	}		public ApiResultItem edit(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		CommonItem result =  feign.edit(item);		return new ApiResultItem(result ,commonUtil.toPojo(result));	}		public ApiResultItem list(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		CommonItem result =  feign.list(item);		return  new ApiResultItem(result ,commonUtil.toPojoList(result));	}		public ApiResultItem delete(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		CommonItem result =  feign.delete(item);		return new ApiResultItem(result ,commonUtil.toPojo(result));	}		public ApiResultItem login(CommonItem item) throws Exception{		QueryItem query = queryItemCommonUtil.toPojo(item);		CommonItem result =  feign.login(item);		return new ApiResultItem(result ,commonUtil.toPojo(result));	}}