package com.mg.website.service.convert.base;import com.mg.common.pojo.CommonData;import com.mg.common.pojo.CommonItem;import com.mg.website.common.service.convert.CommonItemUtils;import com.mg.website.db.pojo.RmtUserInfo;import java.util.ArrayList;import java.util.List;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class RmtUserInfoCommonBase implements CommonItemUtils<RmtUserInfo> {	Logger logger = LoggerFactory.getLogger(getClass());		private CommonData toCommonData(RmtUserInfo pojo) {		CommonData data = new CommonData();		return data;	}		private RmtUserInfo toPojoData( CommonData data) {		RmtUserInfo pojo = new RmtUserInfo();		return pojo;	}		public CommonItem toCommon(RmtUserInfo pojo) {		return success(toCommonData(pojo));	}		public  CommonItem toCommon( List<RmtUserInfo> pojos) {		List<CommonData> result = new ArrayList();		for(RmtUserInfo item : pojos){			result.add(toCommonData(item));		}		return success(result);	}		public RmtUserInfo toPojo(CommonItem item) throws Exception{		checkCommonItem(item);		List<CommonData> datas = item.getDatas();		if(datas ==null||datas.size()==0){logger.debug("CommonItem 中data数据为空!!"); return null;}		if(datas.size()>1){logger.debug("CommonItem 中data数据不止一条数据!!"); }		return toPojoData(datas.get(0));	}		public List<RmtUserInfo> toPojoList(CommonItem item) throws Exception{		checkCommonItem(item);		List<RmtUserInfo> result = new ArrayList();			List<CommonData> datas = item.getDatas();			for(CommonData data : datas){				result.add(toPojoData(data));			}			return result;		}				}